frappe.ui.form.on('Water Height Measurement', {
    //console.dir('--------------------Flow calc')
     'height': function(frm){
        var TotalSeconds=(24*60*60/1000); // # of seconds in a day, divided by 1000 to convert to MLD
        var water_height = flt(frm.doc.height)/100  // converting from cm to m
        //
        var L=flt(frm.doc.crest_length);
        var FlowPerSec=0;
        var wier_type = frm.doc.biox_wier_notch_type;
        // console.dir(wier_type);console.dir(L); console.dir(water_height);console.dir(water_height**1.5)
        if(wier_type=='Rectangular'){
            FlowPerSec = 1.85*L*(water_height**1.5); console.dir(FlowPerSec)

        } else {
            FlowPerSec = 1.42*(water_height**2.5);

        }
        //console.dir(FlowPerSec); console.dir(TotalSeconds)
        frm.set_value("calculated_daily_volume", flt(FlowPerSec)*flt(TotalSeconds));

    }


});


