frappe.ui.form.on('Daily Dosage', {
    dosage_structure: function(frm, cdt, cdn) {
        frm.clear_table("daily_dosage_values"); 
        console.dir(frm.doc.dosage_structure);
        frappe.model.with_doc("Lab Test Structure",frm.doc.dosage_structure, function() {
            var source_doc=frappe.model.get_doc("Lab Test Structure",frm.doc.dosage_structure);
            console.dir(source_doc);
            $.each(source_doc.dosage_structure, function(index, source_row){
                console.dir(source_row.effective_from_date);
                console.dir(frm.doc.dosage_date_time);
                if (source_row.is_active==1 && source_row.effective_from_date<=frm.doc.dosage_date_time){
                    var d = frm.add_child('daily_dosage_values')
                    d.dosing_product = source_row.dosing_product;
                    d.reference_dosing_amount = source_row.dosage_amount;
                }
                //d.remarks = 'Effective from:'.concat(source_row.effective_date.toString);
            });cur_frm.refresh_field('daily_dosage_values');

        });
    }


});

